sys_script_include.script:
var FillEmployeeData = Class.create();
FillEmployeeData.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    callback: function() {
        var user = this.getParameter('sysparm_user');
        var gr = new GlideRecord('x_448130_corpora_0_employee');
        gr.addQuery('sys_id', user);
        gr.query();
       gs.debug('E-voucher ='+gr.getRowCount);
        if (gr.next()) {
            return gr.project_account;
        }
    },

    type: 'FillEmployeeData'
});
